mapper.select:
select
from mes_wms_stockinoutbatch
is_valid = '1'
and id = #{id}
and stock_in_out_id = #{stockInOutId}
and stock_in_out_code like concat('%',#{stockInOutCode},'%')
and stock_in_out_detail_id = #{stockInOutDetailId}
and stock_id = #{stockId}
and stock_code like concat('%',#{stockCode},'%')
and stock_name like concat('%',#{stockName},'%')
and allot_id = #{allotId}
and stock_place_id = #{stockPlaceId}
and stock_place_code like concat('%',#{stockPlaceCode},'%')
and stock_place_name like concat('%',#{stockPlaceName},'%')
and tray_id = #{trayId}
and tray_code like concat('%',#{trayCode},'%')
and material_id = #{materialId}
and material_code like concat('%',#{materialCode},'%')
and material_short_name like concat('%',#{materialShortName},'%')
and material_spec like concat('%',#{materialSpec},'%')
and units_id = #{unitsId}
and units_name like concat('%',#{unitsName},'%')
and bar_code like concat('%',#{barCode},'%')
and in_batch_no like concat('%',#{inBatchNo},'%')
and batch_no like concat('%',#{batchNo},'%')
and qty
and k_f_period = #{kFPeriod}
and k_f_date
and period_date
and suppliar_id = #{suppliarId}
and suppliar_code like concat('%',#{suppliarCode},'%')
and suppliar_name like concat('%',#{suppliarName},'%')
and source_code like concat('%',#{sourceCode},'%')
and stock_in_id = #{stockInId}
and stock_in_code like concat('%',#{stockInCode},'%')
and stock_in_list_id = #{stockInListId}
and state like concat('%',#{state},'%')
and picking_state like concat('%',#{pickingState},'%')
and car_num like concat('%',#{carNum},'%')
and client_id = #{clientId}
and is_car like concat('%',#{isCar},'%')
and buy_apply_invoice_id = #{buyApplyInvoiceId}
and buy_apply_invoice_list_id = #{buyApplyInvoiceListId}
and organization_id = #{organizationId}
and organization_code like concat('%',#{organizationCode},'%')
and organization_name like concat('%',#{organizationName},'%')
and f_company_id = #{fCompanyId}
and f_en_code like concat('%',#{fEnCode},'%')
and f_short_name like concat('%',#{fShortName},'%')
and client_code like concat('%',#{clientCode},'%')
and client_name like concat('%',#{clientName},'%')
and create_id = #{createId}
and create_name like concat('%',#{createName},'%')
and create_time
and update_id = #{updateId}
and update_name like concat('%',#{updateName},'%')
and update_time
and remark like concat('%',#{remark},'%')
and is_valid like concat('%',#{isValid},'%')
order by id desc

select
  id, stock_in_out_id, stock_in_out_code, stock_in_out_detail_id, stock_id, stock_code, stock_name, allot_id, stock_place_id, stock_place_code, stock_place_name, tray_id, tray_code, material_id, material_code, material_short_name, material_spec, units_id, units_name, bar_code, in_batch_no, batch_no, qty, k_f_period, k_f_date, period_date, suppliar_id, suppliar_code, suppliar_name, source_code, stock_in_id, stock_in_code, stock_in_list_id, state, picking_state, car_num, client_id, is_car, buy_apply_invoice_id, buy_apply_invoice_list_id, organization_id, organization_code, organization_name, f_company_id, f_en_code, f_short_name, client_code, client_name, create_id, create_name, create_time, update_id, update_name, update_time, remark, is_valid
from mes_wms_stockinoutbatch

is_valid = '1'

        and id = #{id}


        and stock_in_out_id = #{stockInOutId}


        and stock_in_out_code = #{stockInOutCode}


        and stock_in_out_detail_id = #{stockInOutDetailId}


        and stock_id = #{stockId}


        and stock_code = #{stockCode}


        and stock_name = #{stockName}


        and allot_id = #{allotId}


        and stock_place_id = #{stockPlaceId}


        and stock_place_code = #{stockPlaceCode}


        and stock_place_name = #{stockPlaceName}


        and tray_id = #{trayId}


        and tray_code = #{trayCode}


        and material_id = #{materialId}


        and material_code = #{materialCode}


        and material_short_name = #{materialShortName}


        and material_spec = #{materialSpec}


        and units_id = #{unitsId}


        and units_name = #{unitsName}


        and bar_code = #{barCode}


        and in_batch_no = #{inBatchNo}


        and batch_no = #{batchNo}


        and qty = #{qty}


        and k_f_period = #{kFPeriod}


        and k_f_date = #{kFDate}


        and period_date = #{periodDate}


        and suppliar_id = #{suppliarId}


        and suppliar_code = #{suppliarCode}


        and suppliar_name = #{suppliarName}


        and source_code = #{sourceCode}


        and stock_in_id = #{stockInId}


        and stock_in_code = #{stockInCode}


        and stock_in_list_id = #{stockInListId}


        and state = #{state}


        and picking_state = #{pickingState}


        and car_num = #{carNum}


        and client_id = #{clientId}


        and is_car = #{isCar}


        and buy_apply_invoice_id = #{buyApplyInvoiceId}


        and buy_apply_invoice_list_id = #{buyApplyInvoiceListId}


        and organization_id = #{organizationId}


        and organization_code = #{organizationCode}


        and organization_name = #{organizationName}


        and f_company_id = #{fCompanyId}


        and f_en_code = #{fEnCode}


        and f_short_name = #{fShortName}


        and client_code = #{clientCode}


        and client_name = #{clientName}


        and create_id = #{createId}


        and create_name = #{createName}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_name = #{updateName}


        and update_time = #{updateTime}


        and remark = #{remark}


        and is_valid = #{isValid}


limit #{pageable.offset}, #{pageable.pageSize}

