mapper.select:
select
from mes_wms_stockinoutlist
is_valid = '1'
and id = #{id}
and stock_in_out_id = #{stockInOutId}
and material_id = #{materialId}
and material_code like concat('%',#{materialCode},'%')
and material_short_name like concat('%',#{materialShortName},'%')
and material_spec like concat('%',#{materialSpec},'%')
and units_id = #{unitsId}
and units_name like concat('%',#{unitsName},'%')
and stock_id = #{stockId}
and stock_code like concat('%',#{stockCode},'%')
and stock_name like concat('%',#{stockName},'%')
and import_qty
and f_pallet
and f_finished
and f_count = #{fCount}
and stock_qty
and invoice_qty
and sup_statement_list_id = #{supStatementListId}
and sup_statement_id = #{supStatementId}
and verification_state like concat('%',#{verificationState},'%')
and back_qty
and price
and amount
and tax_rate
and tax_amount
and tax_price
and all_amount
and k_f_period = #{kFPeriod}
and k_f_date like concat('%',#{kFDate},'%')
and period_date like concat('%',#{periodDate},'%')
and contract_id = #{contractId}
and contract_code like concat('%',#{contractCode},'%')
and contract_list_id = #{contractListId}
and source_type like concat('%',#{sourceType},'%')
and source_id = #{sourceId}
and source_code like concat('%',#{sourceCode},'%')
and source_list_id = #{sourceListId}
and order_id = #{orderId}
and order_code like concat('%',#{orderCode},'%')
and order_list_id = #{orderListId}
and task_id = #{taskId}
and task_code like concat('%',#{taskCode},'%')
and task_list_id = #{taskListId}
and sale_apply_invoice_id = #{saleApplyInvoiceId}
and sale_apply_invoice_list_id = #{saleApplyInvoiceListId}
and e_r_p_spe_stock like concat('%',#{eRPSpeStock},'%')
and e_r_p_of_object like concat('%',#{eRPOfObject},'%')
and checker_id = #{checkerId}
and checker_name like concat('%',#{checkerName},'%')
and checker_time
and line_numbe like concat('%',#{lineNumbe},'%')
and organization_id = #{organizationId}
and organization_code like concat('%',#{organizationCode},'%')
and organization_name like concat('%',#{organizationName},'%')
and create_id = #{createId}
and create_name like concat('%',#{createName},'%')
and create_time
and update_id = #{updateId}
and update_name like concat('%',#{updateName},'%')
and update_time
and remark like concat('%',#{remark},'%')
and is_valid like concat('%',#{isValid},'%')
and company_id = #{companyId}
and company_code like concat('%',#{companyCode},'%')
and company_name like concat('%',#{companyName},'%')
and in_batch_no like concat('%',#{inBatchNo},'%')
and batch_no like concat('%',#{batchNo},'%')
and stock_place_id = #{stockPlaceId}
and stock_place_code like concat('%',#{stockPlaceCode},'%')
and stock_place_name like concat('%',#{stockPlaceName},'%')
and bar_code like concat('%',#{barCode},'%')
and status like concat('%',#{status},'%')
and buy_accept_list_id = #{buyAcceptListId}
and suppliar_id = #{suppliarId}
and suppliar_code like concat('%',#{suppliarCode},'%')
and suppliar_name like concat('%',#{suppliarName},'%')
and work_report_code like concat('%',#{workReportCode},'%')
and work_report_id = #{workReportId}
and work_report_time
order by id desc

select
  id, stock_in_out_id, material_id, material_code, material_short_name, material_spec, units_id, units_name, stock_id, stock_code, stock_name, import_qty, f_pallet, f_finished, f_count, stock_qty, invoice_qty, sup_statement_list_id, sup_statement_id, verification_state, back_qty, price, amount, tax_rate, tax_amount, tax_price, all_amount, k_f_period, k_f_date, period_date, contract_id, contract_code, contract_list_id, source_type, source_id, source_code, source_list_id, order_id, order_code, order_list_id, task_id, task_code, task_list_id, sale_apply_invoice_id, sale_apply_invoice_list_id, e_r_p_spe_stock, e_r_p_of_object, checker_id, checker_name, checker_time, line_numbe, organization_id, organization_code, organization_name, create_id, create_name, create_time, update_id, update_name, update_time, remark, is_valid, company_id, company_code, company_name, in_batch_no, batch_no, stock_place_id, stock_place_code, stock_place_name, bar_code, status, buy_accept_list_id, suppliar_id, suppliar_code, suppliar_name, work_report_code, work_report_id, work_report_time
