mapper.select:
select
  id, stock_current_sum_id, stock_current_id, stock_id, stock_code, stock_name, stock_place_id_source, stock_place_code_source, stock_place_name_source, stock_place_id_new, stock_place_code_new, stock_place_name_new, in_batch_no, batch_no, bar_code, material_id, material_code, material_short_name, material_spec, qty, qty_allot, qty_useable, qty_freeze, suppliar_id, suppliar_code, suppliar_full_name, move_place_date, disposer, department_id, department_code, department_name, organization_id, organization_code, organization_name, f_company_id, f_en_code, f_short_name, create_id, create_name, create_time, update_id, update_name, update_time, remark, is_valid, posting_place_date, state, move_type, move_code, units_id, units_name, disposer_id
from mes_wms_stockmoveplace

is_valid = '1'

        and id = #{id}


        and stock_current_sum_id = #{stockCurrentSumId}


        and stock_current_id = #{stockCurrentId}


        and stock_id = #{stockId}


        and stock_code = #{stockCode}


        and stock_name = #{stockName}


        and stock_place_id_source = #{stockPlaceIdSource}


        and stock_place_code_source = #{stockPlaceCodeSource}


        and stock_place_name_source = #{stockPlaceNameSource}


        and stock_place_id_new = #{stockPlaceIdNew}


        and stock_place_code_new = #{stockPlaceCodeNew}


        and stock_place_name_new = #{stockPlaceNameNew}


        and in_batch_no = #{inBatchNo}


        and batch_no = #{batchNo}


        and bar_code = #{barCode}


        and material_id = #{materialId}


        and material_code = #{materialCode}


        and material_short_name = #{materialShortName}


        and material_spec = #{materialSpec}


        and qty = #{qty}


        and qty_allot = #{qtyAllot}


        and qty_useable = #{qtyUseable}


        and qty_freeze = #{qtyFreeze}


        and suppliar_id = #{suppliarId}


        and suppliar_code = #{suppliarCode}


        and suppliar_full_name = #{suppliarFullName}


        and move_place_date = #{movePlaceDate}


        and disposer = #{disposer}


        and department_id = #{departmentId}


        and department_code = #{departmentCode}


        and department_name = #{departmentName}


        and organization_id = #{organizationId}


        and organization_code = #{organizationCode}


        and organization_name = #{organizationName}


        and f_company_id = #{fCompanyId}


        and f_en_code = #{fEnCode}


        and f_short_name = #{fShortName}


        and create_id = #{createId}


        and create_name = #{createName}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_name = #{updateName}


        and update_time = #{updateTime}


        and remark = #{remark}


        and is_valid = #{isValid}


        and posting_place_date = #{postingPlaceDate}


        and state = #{state}


        and move_type = #{moveType}


        and move_code = #{moveCode}


        and units_id = #{unitsId}


        and units_name = #{unitsName}


        and disposer_id = #{disposerId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mes_wms_stockmoveplace

is_valid = '1'

        and id = #{id}


        and stock_current_sum_id = #{stockCurrentSumId}


        and stock_current_id = #{stockCurrentId}


