mapper.select:
select
from mes_wms_stockpo
is_valid = '1'
and id = #{id}
and stock_po_code like concat('%',#{stockPoCode},'%')
and year like concat('%',#{year},'%')
and out_stock_id = #{outStockId}
and out_stock_code like concat('%',#{outStockCode},'%')
and out_stock_name like concat('%',#{outStockName},'%')
and in_stock_id = #{inStockId}
and in_stock_code like concat('%',#{inStockCode},'%')
and in_stock_name like concat('%',#{inStockName},'%')
and client_id = #{clientId}
and client_code like concat('%',#{clientCode},'%')
and client_name like concat('%',#{clientName},'%')
and out_type_id = #{outTypeId}
and out_bill_code like concat('%',#{outBillCode},'%')
and in_type_id = #{inTypeId}
and in_bill_code like concat('%',#{inBillCode},'%')
and po_type like concat('%',#{poType},'%')
and state like concat('%',#{state},'%')
and department_id = #{departmentId}
and department_code like concat('%',#{departmentCode},'%')
and department_name like concat('%',#{departmentName},'%')
and emp_id = #{empId}
and emp_code like concat('%',#{empCode},'%')
and emp_name like concat('%',#{empName},'%')
and manager_id = #{managerId}
and manager_code like concat('%',#{managerCode},'%')
and manager_name like concat('%',#{managerName},'%')
and checker_id = #{checkerId}
and checker_name like concat('%',#{checkerName},'%')
and checker_time
and f_company_id = #{fCompanyId}
and f_en_code like concat('%',#{fEnCode},'%')
and f_short_name like concat('%',#{fShortName},'%')
and create_id = #{createId}
and create_name like concat('%',#{createName},'%')
and create_time
and update_id = #{updateId}
and update_name like concat('%',#{updateName},'%')
and update_time
and remark like concat('%',#{remark},'%')
and is_valid like concat('%',#{isValid},'%')
and out_company_id = #{outCompanyId}
and out_company_name like concat('%',#{outCompanyName},'%')
and in_company_id = #{inCompanyId}
and in_company_name like concat('%',#{inCompanyName},'%')
and disposer like concat('%',#{disposer},'%')
and period like concat('%',#{period},'%')
and stock_current_sum_id = #{stockCurrentSumId}
and work_order_code like concat('%',#{workOrderCode},'%')
and po_bill_date like concat('%',#{poBillDate},'%')
and po_posting_date like concat('%',#{poPostingDate},'%')
and disposer_id = #{disposerId}
and sap_id = #{sapId}
and sap_code like concat('%',#{sapCode},'%')
order by id desc

select
  id, stock_po_code, year, out_stock_id, out_stock_code, out_stock_name, in_stock_id, in_stock_code, in_stock_name, client_id, client_code, client_name, out_type_id, out_bill_code, in_type_id, in_bill_code, po_type, state, department_id, department_code, department_name, emp_id, emp_code, emp_name, manager_id, manager_code, manager_name, checker_id, checker_name, checker_time, f_company_id, f_en_code, f_short_name, create_id, create_name, create_time, update_id, update_name, update_time, remark, is_valid, out_company_id, out_company_name, in_company_id, in_company_name, disposer, period, stock_current_sum_id, work_order_code, po_bill_date, po_posting_date, disposer_id, sap_id, sap_code
