mapper.select:
select
  id, stock_po_id, stock_po_code, stock_po_list_id, material_id, material_code, material_short_name, material_spec, out_stock_id, out_stock_code, out_stock_name, in_stock_id, in_stock_code, in_stock_name, out_stock_place_id, out_stock_place_code, out_stock_place_name, in_stock_place_id, in_stock_place_code, in_stock_place_name, bar_code, in_batch_no, batch_no, qty, units_id, units_name, in_out_type_propert, state, suppliar_id, suppliar_code, suppliar_name, client_id, client_code, client_name, carrier_id, carrier_name, car_num, is_car, f_company_id, f_en_code, f_short_name, create_id, create_name, create_time, update_id, update_name, update_time, remark, is_valid
from mes_wms_stockpobatch

is_valid = '1'

        and id = #{id}


        and stock_po_id = #{stockPoId}


        and stock_po_code = #{stockPoCode}


        and stock_po_list_id = #{stockPoListId}


        and material_id = #{materialId}


        and material_code = #{materialCode}


        and material_short_name = #{materialShortName}


        and material_spec = #{materialSpec}


        and out_stock_id = #{outStockId}


        and out_stock_code = #{outStockCode}


        and out_stock_name = #{outStockName}


        and in_stock_id = #{inStockId}


        and in_stock_code = #{inStockCode}


        and in_stock_name = #{inStockName}


        and out_stock_place_id = #{outStockPlaceId}


        and out_stock_place_code = #{outStockPlaceCode}


        and out_stock_place_name = #{outStockPlaceName}


        and in_stock_place_id = #{inStockPlaceId}


        and in_stock_place_code = #{inStockPlaceCode}


        and in_stock_place_name = #{inStockPlaceName}


        and bar_code = #{barCode}


        and in_batch_no = #{inBatchNo}


        and batch_no = #{batchNo}


        and qty = #{qty}


        and units_id = #{unitsId}


        and units_name = #{unitsName}


        and in_out_type_propert = #{inOutTypePropert}


        and state = #{state}


        and suppliar_id = #{suppliarId}


        and suppliar_code = #{suppliarCode}


        and suppliar_name = #{suppliarName}


        and client_id = #{clientId}


        and client_code = #{clientCode}


        and client_name = #{clientName}


        and carrier_id = #{carrierId}


        and carrier_name = #{carrierName}


        and car_num = #{carNum}


        and is_car = #{isCar}


        and f_company_id = #{fCompanyId}


        and f_en_code = #{fEnCode}


        and f_short_name = #{fShortName}


        and create_id = #{createId}


        and create_name = #{createName}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_name = #{updateName}


        and update_time = #{updateTime}


        and remark = #{remark}


        and is_valid = #{isValid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mes_wms_stockpobatch

is_valid = '1'

        and id = #{id}


        and stock_po_id = #{stockPoId}


        and stock_po_code = #{stockPoCode}


        and stock_po_list_id = #{stockPoListId}


        and material_id = #{materialId}


        and material_code = #{materialCode}


        and material_short_name = #{materialShortName}


        and material_spec = #{materialSpec}


        and out_stock_id = #{outStockId}


        and out_stock_code = #{outStockCode}


        and out_stock_name = #{outStockName}


        and in_stock_id = #{inStockId}


        and in_stock_code = #{inStockCode}


        and in_stock_name = #{inStockName}


        and out_stock_place_id = #{outStockPlaceId}


        and out_stock_place_code = #{outStockPlaceCode}


        and out_stock_place_name = #{outStockPlaceName}


        and in_stock_place_id = #{inStockPlaceId}


        and in_stock_place_code = #{inStockPlaceCode}


        and in_stock_place_name = #{inStockPlaceName}


        and bar_code = #{barCode}


        and in_batch_no = #{inBatchNo}


        and batch_no = #{batchNo}


        and qty = #{qty}


        and units_id = #{unitsId}


        and units_name = #{unitsName}


        and in_out_type_propert = #{inOutTypePropert}


        and state = #{state}


        and suppliar_id = #{suppliarId}


        and suppliar_code = #{suppliarCode}


        and suppliar_name = #{suppliarName}


        and client_id = #{clientId}


        and client_code = #{clientCode}


        and client_name = #{clientName}


        and carrier_id = #{carrierId}


        and carrier_name = #{carrierName}


        and car_num = #{carNum}


        and is_car = #{isCar}


        and f_company_id = #{fCompanyId}


        and f_en_code = #{fEnCode}


        and f_short_name = #{fShortName}


        and create_id = #{createId}


        and create_name = #{createName}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_name = #{updateName}


        and update_time = #{updateTime}


        and remark = #{remark}


        and is_valid = #{isValid}
